import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import axios from 'axios';

interface Lesson {
  id: string;
  name: string;
  description: string;
  content: any;
  estimated_duration_minutes: number;
  lesson_type: string;
}

const LessonPage: React.FC = () => {
  const { lessonId } = useParams<{ lessonId: string }>();
  const [lesson, setLesson] = useState<Lesson | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchLesson = async () => {
      if (!lessonId) return;

      try {
        const response = await axios.get(`/api/lessons/${lessonId}`);
        setLesson(response.data);
      } catch (err) {
        setError('Failed to load lesson. Please try again later.');
        console.error('Error fetching lesson:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchLesson();
  }, [lessonId]);

  const renderLessonContent = (content: any) => {
    if (typeof content === 'string') {
      return <div dangerouslySetInnerHTML={{ __html: content }} />;
    }

    if (content.html) {
      return <div dangerouslySetInnerHTML={{ __html: content.html }} />;
    }

    if (content.markdown) {
      // Simple markdown-to-HTML conversion for basic formatting
      let html = content.markdown
        .replace(/^# (.*$)/gm, '<h1>$1</h1>')
        .replace(/^## (.*$)/gm, '<h2>$1</h2>')
        .replace(/^### (.*$)/gm, '<h3>$1</h3>')
        .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')
        .replace(/\*(.*?)\*/g, '<em>$1</em>')
        .replace(/`(.*?)`/g, '<code>$1</code>')
        .replace(/```([\s\S]*?)```/g, '<pre><code>$1</code></pre>')
        .replace(/\n\n/g, '</p><p>')
        .replace(/^\s*/, '<p>')
        .replace(/\s*$/, '</p>');

      return <div dangerouslySetInnerHTML={{ __html: html }} />;
    }

    // Fallback to JSON display
    return <pre>{JSON.stringify(content, null, 2)}</pre>;
  };

  if (loading) {
    return <div className="loading">Loading lesson...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  if (!lesson) {
    return <div className="error">Lesson not found</div>;
  }

  return (
    <div>
      <div className="breadcrumb">
        <Link to="/">Home</Link>
        <span>›</span>
        <span>Lesson</span>
      </div>

      <div className="lesson-content">
        <h1>{lesson.name}</h1>

        <div style={{
          marginBottom: '2rem',
          padding: '1rem',
          background: '#f8f9fa',
          borderRadius: '8px',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center'
        }}>
          <div>
            <strong>Type:</strong> {lesson.lesson_type}
          </div>
          <div>
            <strong>Duration:</strong> ~{lesson.estimated_duration_minutes} minutes
          </div>
        </div>

        {lesson.description && (
          <div style={{ marginBottom: '2rem', fontStyle: 'italic', color: '#666' }}>
            {lesson.description}
          </div>
        )}

        {renderLessonContent(lesson.content)}
      </div>

      <div style={{ textAlign: 'center', marginTop: '2rem' }}>
        <Link to="/" className="button">
          ← Back to Home
        </Link>
      </div>
    </div>
  );
};

export default LessonPage;
